 * Converts a JSON value to a token-efficient shorthand (TOON-style).
 * Strips quotes, uses simple indentation, and flattens structures.
 */
function toTOON(val, indent = 0) {
  const pad = ' '.repeat(indent);

  if (Array.isArray(val)) {
    if (val.length === 0) return '[]';
    return val.map(item => `\n${pad}- ${toTOON(item, indent + 2)}`).join('');
  }

  if (val !== null && typeof val === 'object') {
    return Object.entries(val)
      .filter(([_, v]) => v !== null && v !== undefined) // Skip nulls to save more tokens
      .map(([k, v]) => `\n${pad}${k}: ${toTOON(v, indent + 2)}`)
      .join('');
  }

  return String(val);
}

